        $("#aspnetForm").parent().append("<div id='SPServices_msgBox' style=" + boxCSS + ">" + msg);
        var height = $("#SPServices_msgBox").height();
        var width = $("#SPServices_msgBox").width();
        var leftVal = ($(window).width() / 2) - (width / 2) + "px";
